import React from 'react';
import { connect } from 'react-redux';
import { Table } from 'react-bootstrap';
import { Link } from "react-router-dom";
import { fetchView } from '../../actions/weather/view'

class View extends React.Component {

    componentDidMount() {
        const { dispatch } = this.props
        dispatch(fetchView(1))
    }

    getImage = view => {
        let image = ''
        if (article.media[0]) {
            image =  view.media[0]['media-metadata'][0].url 
        }
        return image

    }

    getView = (results) => (
        results.map( view =>  
               <tr key={view.id}>
                   <td>
                     <img src={ this.getImage(view) } alt='' />
                   </td> 
                  <td>
                    <div className='responsive'> 
                    <Link  to={`/view/${fetchView.id}`}>  
                        <div className='row'>                      
                            <div className='col-md-11'>{ article.title }</div> 
                            <div className='col-md-1' style={{fontSize: '16px', color: 'gray', fontWeight: 'bold'}}>
                                <i class="fa fa-angle-double-right" aria-hidden="true"></i>
                            </div>                                  
                        </div>   
                        </Link >      
                        <div className='row'>
                            <div className='col-md-9' style={{fontSize: '13px', color: 'gray'}}>
                            { article.abstract }                      
                            </div>
                            <div className='col-md-3' style={{fontSize: '13px', color: 'gray'}}>
                            { article.published_date }
                            </div>                     
                        </div>             
                    </div>    
                  </td>                
               </tr>
            )     
    )

    render () {
           const { 
               articles: { 
                   articles: { 
                       results
                    } = []
                }
                 } = this.props
        return (
            <div>
                <Table responsive> 
                <tbody>
                    { results && this.getView(results) }
                </tbody>     
                </Table>

            </div>
        )
    }
}

const mapStateToProps = (state) => {
    console.log(state)
    return {
        articles: state.view
    }
}

export default connect(mapStateToProps)(View);
